(function ($) {
	$.extend({
		uploadPreview: function (options) {

			// Options + Defaults
			var settings = $.extend({
				input_field: ".image-input",
				preview_box: ".image-preview",
				label_field: ".image-label",
				label_default: "Choose File",
				label_selected: "Change File",
				no_label: false,
				success_callback: null,
			}, options);

			// Check if FileReader is available
			if (window.File && window.FileList && window.FileReader) {
				if (typeof ($(settings.input_field)) !== 'undefined' && $(settings.input_field) !== null) {
					$(settings.input_field).change(function () {
						var files = this.files;

						if (files.length > 0) {
							var file = files[0];
							var reader = new FileReader();

							// Load file
							reader.addEventListener("load", function (event) {
								var loadedFile = event.target;
								
								// Check format
								if (file.type.match('image')) {
									// Image
									$(settings.preview_box).css("background-image", "url(" + loadedFile.result + ")");
									$(settings.preview_box).css("background-size", "cover");
									$(settings.preview_box).css("background-position", "center center");

									$('#err-message').empty();
									$('#err-message').addClass('hide');
								} else {
									if (settings.no_label == false) {
										// Change label
										$(settings.label_field).html(settings.label_default);
									}

									// Clear background
									$(settings.preview_box).css("background-image", "none");
									$('#err-message').html('Incorrect file format. Please select an image file and try again. Thank you!');
									$('#err-message').removeClass('hide');
								}
							});

							if (settings.no_label == false) {
								// Change label
								$(settings.label_field).html(settings.label_selected);
							}

							// Read the file
							reader.readAsDataURL(file);

							// Success callback function call
							if (settings.success_callback) {
								settings.success_callback();
							}
						} else {
							if (settings.no_label == false) {
								// Change label
								$(settings.label_field).html(settings.label_default);
							}

							// Clear background
							$(settings.preview_box).css("background-image", "none");

							$('#err-message').html('Please select a profile image for your biodata. Biodata without a profile image are treated as incomplete biodata and hence will not be considered for approvals.');
							$('#err-message').removeClass('hide');
						}
					});
				}
			} else {
				$('#err-message').html('You need a browser with file reader support, to use this form properly. Please use another browser with proper file reader support or update the verison of the current browser in use. Thank you!');
				$('#err-message').removeClass('hide');
				return false;
			}
		}
	});
})(jQuery);